package com.app.mvc.acl.dao;import com.app.mvc.acl.domain.SysUser;import com.app.mvc.beans.PageQuery;import com.app.mvc.common.DatabaseRepository;import org.apache.ibatis.annotations.Param;import java.util.List;/** * Created by jimin on 16/1/18. */@DatabaseRepositorypublic interface SysUserDao {    void save(SysUser sysUser);    void update(SysUser sysUser);    void updatePassword(@Param("id") int id, @Param("password") String password);    SysUser findById(@Param("id") int id);    SysUser findByUsername(@Param("username") String username);    List<SysUser> getPageByDeptId(@Param("deptId") int deptId, @Param("page") PageQuery page);    int countByDeptId(@Param("deptId") int deptId);    List<SysUser> getByIdList(@Param("idList") List<Integer> idList);    List<SysUser> getBySupplierId(@Param("supplierId") int supplierId);}