package com.app.mvc.acl.service;import com.app.mvc.acl.convert.BaseConvert;import com.app.mvc.acl.convert.UserConvert;import com.app.mvc.acl.dao.SysUserDao;import com.app.mvc.acl.domain.SysUser;import com.app.mvc.acl.vo.UserPara;import com.app.mvc.beans.PageQuery;import com.app.mvc.beans.PageResult;import com.google.common.base.Preconditions;import org.apache.commons.lang3.StringUtils;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;/** * Created by jimin on 16/1/18. */@Servicepublic class SysUserService implements SysService {    @Resource    private SysUserDao sysUserDao;    @Resource    private SysLogService sysLogService;    /**     * 新增管理员     */    public void save(UserPara para) {        BaseConvert.checkPara(para);        SysUser user = UserConvert.of(para);        sysUserDao.save(user);        sysLogService.saveUserLog(null, user);    }    /**     * 更新用户     */    public void update(UserPara para) {        BaseConvert.checkPara(para);        SysUser before = sysUserDao.findById(para.getId());        Preconditions.checkNotNull(before, "待更新用户不存在");        SysUser after = UserConvert.of(para);        sysUserDao.update(after);        sysLogService.saveUserLog(before, after);    }    /**     * 根据用户名获取用户信息     */    public SysUser findByUsername(String username) {        if (StringUtils.isBlank(username)) {            return null;        }        return sysUserDao.findByUsername(username);    }    /**     * 分页获取部门下用户信息     */    public PageResult<SysUser> getPageByDeptId(int deptId, PageQuery page) {        BaseConvert.checkPara(page);        int count = sysUserDao.countByDeptId(deptId);        if (count > 0) {            List<SysUser> list = sysUserDao.getPageByDeptId(deptId, page);            return PageResult.<SysUser>builder().total(count).data(list).build();        }        return PageResult.<SysUser>builder().total(0).build();    }    public SysUser findById(int id) {        return sysUserDao.findById(id);    }    @Override    public void recover(int targetId, Object o) {        SysUser user = (SysUser) o;        Preconditions.checkNotNull(user, "还原前的用户为空,无法还原");        SysUser before = sysUserDao.findById(targetId);        Preconditions.checkNotNull(before, "待还原的用户不存在");        sysUserDao.update(user);        sysLogService.saveUserLog(before, user);    }}