package com.app.mvc.schedule;import lombok.extern.slf4j.Slf4j;import org.quartz.Job;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;/** * Created by jimin on 16/5/8. */@Slf4jpublic abstract class AbstractScheduleJob implements Job {    @Override    public void execute(JobExecutionContext context) throws JobExecutionException {        log.info("定时任务[{}]开始执行", scheduleId());        schedule(context);        log.info("定时任务[{}]执行结束", scheduleId());    }    public abstract void schedule(JobExecutionContext context) throws JobExecutionException;    public abstract String scheduleId();}