package com.app.mvc.schedule;import org.quartz.Job;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;import java.text.SimpleDateFormat;import java.util.Calendar;/** * Created by jimin on 16/5/8. */public class ScheduledJob implements Job {    @Override    public void execute(JobExecutionContext context) throws JobExecutionException {        String jobName = context.getJobDetail().getKey().getName();        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        System.out.println("任务Key:" + jobName + " 正在执行，执行时间: " + dateFormat.format(Calendar.getInstance().getTime()));    }}