package com.app.mvc.test;/** * Created by jimin on 16/7/5. */public class TestMerge {    public static void main(String[] args) {        ListNode temp = null;        ListNode l1 = new ListNode(1);        temp = l1;        temp.next = new ListNode(3);        temp = temp.next;        temp.next = new ListNode(5);        temp = temp.next;        temp.next = new ListNode(7);        temp = temp.next;        temp.next = new ListNode(8);        ListNode l2 = new ListNode(2);        temp = l2;        temp.next = new ListNode(4);        temp = temp.next;        temp.next = new ListNode(6);        temp = temp.next;        temp.next = new ListNode(7);        temp = temp.next;        temp.next = new ListNode(9);        ListNode result = new TestMerge().mergeTwoLists(l1, l2);        while(result != null) {            System.out.println(result.val);            result = result.next;        }    }    public ListNode mergeTwoLists(ListNode l1, ListNode l2) {        if (l1 == null) {            return l2;        }        if (l2 == null) {            return l1;        }        ListNode l3 = null;        ListNode next;        ListNode temp = null;        while (l1 != null && l2 != null) {            if (l1.val < l2.val) {                next = new ListNode(l1.val);                l1 = l1.next;            } else {                next = new ListNode(l2.val);                l2 = l2.next;            }            if (l3 == null) {                l3 = next;                temp = l3;            } else {                temp.next = next;                temp = temp.next;            }        }        while (l1 != null) {            next = new ListNode(l1.val);            temp.next = next;            temp = temp.next;            l1 = l1.next;        }        while (l2 != null) {            next = new ListNode(l2.val);            temp.next = next;            temp = temp.next;            l2 = l2.next;        }        return l3;    }    public static class ListNode {        int val;        ListNode next;        ListNode(int x) {            val = x;            next = null;        }    }}