package com.app.mvc.test;import com.app.mvc.beans.JsonMapper;import com.google.common.collect.Maps;import lombok.extern.slf4j.Slf4j;import java.lang.reflect.Field;import java.util.ArrayList;import java.util.HashMap;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;/** * Created by jimin on 16/3/25. */@Slf4jpublic class TestReflected {    public static void main(String[] args) throws Exception {        Class<TestData> clazz = TestData.class;        Field m = clazz.getDeclaredField("threadPoolExecutorMap");        m.setAccessible(true);        m.set(TestData.class, Maps.newConcurrentMap());    }}