package com.app.mvc.test;import com.app.mvc.beans.JsonMapper;import com.google.common.collect.Maps;import java.util.Map;import java.util.concurrent.ConcurrentMap;/** * Created by jimin on 16/3/28. */public class TestString {    public static void main(String[] args) {        String test = "otaHttpClient.threadPool.setting.123123";        System.out.println(test.substring("otaHttpClient.threadPool.setting.".length()));        ConcurrentMap<Integer, String> concurrentMap = Maps.newConcurrentMap();        for (int i = 1; i < 10; i++) {            concurrentMap.put(i, "yes");        }        for(Map.Entry<Integer, String> entry : concurrentMap.entrySet()) {            if(entry.getKey() % 2 == 0) {                concurrentMap.remove(entry.getKey());            }        }        System.out.println(JsonMapper.obj2String(concurrentMap));    }}